import React, { Component, Fragment } from 'react'
import TextFieldGroup from '../common/TextFieldGroup';
import './TOdos.css'
class Todos extends Component {
    constructor(props) {
        super(props);
        this.state = {
            todos:      '',
            todoArray:  ["vivek", "ededwe","dwe"],
            editTodos: false,
            taskDone:  '',
            indexItem: []
        }
        this.onChange = this.onChange.bind(this)
        this.onSubmit = this.onSubmit.bind(this)
    }
    onChange(e) {
        this.setState({[e.target.name]: e.target.value})
    }
    onSubmit(e) {
        e.preventDefault()
        const id = Math.floor(Math.random()*90000) + 10000
        console.log(id)
        this.setState({editTodos: false})
        if(this.state.todos === ''){
            alert("Please enter value")
        } else {
            this.state.todoArray.push(this.state.todos, id)
            this.state.indexItem.push(id)
        this.setState({ todos: '' })
        }        
    }
    onClickDelete(index) {
       this.state.todoArray.splice(index, 1)
       this.setState({todos: ''})
    //    this.state.indexItem.includes(index)
    // if(this.state.indexItem.includes(index) === true){
    //     this.state.indexItem.splice(index, 1)
    // } else {

    // }
    //    this.state.indexItem.splice(index, 1)
    }
    onClickEdit(index, res) {

        this.setState({taskDone: "list-group-item-success"})
        this.state.indexItem.push(index)

        // this.setState({editTodos: true, todos: res})
        // this.state.todoArray.splice(index, 1)
        // if(this.state.todos !== ''){
        //     console.log("wefew",this.state.todos)
        //     this.state.todoArray.splice(index, 1, this.state.todos)
        // } else {
        //     alert(index)            
        // }
        // console.log("After", index, res, this.state.todos)
    }

    render() {
        console.log(this.state.todoArray)
        if(this.state.editTodos === true){
            return <form onSubmit={this.onSubmit}>                                
                        <TextFieldGroup 
                            placeholder="What do you need to do?"
                            name="todos"
                            value={this.state.todos}
                            onChange={this.onChange}
                        />
                        <input type="submit" className="btn btn-primary" value="Save item" />
                    </form>
        }
        return(
            <Fragment>
                <div className="container">
                    <div className="row">
                        <div className="col-md-6 m-auto">
                            <div style={{background: "#cccc"}}>
                                <h4>Task</h4>
                                {this.state.todoArray.map((res, index) => (
                                    <ul className="list-group" key={index}>
                                        <li className={`list-group-item d-flex  justify-content-between align-items-center ${this.state.indexItem[index] === index ? this.state.taskDone : ''}`}>
                                            <span className="d-flex align-items-start">{res}</span>
                                            <span title="Edit Item" className="btn btn-sm btn-success" onClick={this.onClickEdit.bind(this, index, res)}>&#x2713;</span>
                                            <span title="Revmove Item" className="fa fa-close btn btn-sm btn-danger" onClick={this.onClickDelete.bind(this, index)}></span>
                                        </li>                                
                                    </ul>
                                ))}
                                <div>
                                    <form onSubmit={this.onSubmit}>                                
                                        <TextFieldGroup 
                                            placeholder="What do you need to do?"
                                            name="todos"
                                            value={this.state.todos}
                                            onChange={this.onChange}
                                        />
                                        <input type="submit" className="btn btn-primary" value="Save item" />
                                    </form>
                                </div>       
                            </div>                         
                        </div>
                    </div>
                </div>
            </Fragment>
        )
    }
}
export default Todos
