import React, {Component } from "react"
import ReactDOM from 'react-dom';
class TodoForm extends Component {
    constructor(props){
        super(props)
        this.state = {
            task: ''
        }

        this.doSubmit = this.doSubmit.bind(this)
        this.onChange = this.onChange.bind(this)
    }
    onChange(e){
        this.setState({[e.target.name]: e.target.value})
    }
    doSubmit(e) {
        e.preventDefault();
        // const task = {
        //     task: this.state.task
        // }
        var task = ReactDOM.findDOMNode(this.refs.task).value.trim();
        if (!task) {
            return;
        }
        this.props.onTaskSubmit(task);
        task = ReactDOM.findDOMNode(this.refs.task).value = '';
        this.setState({task: ''})
        return;
    }
    render(){
        return (
            <div className="commentForm vert-offset-top-2">
                <hr />
                <div className="clearfix">
                    <form className="todoForm form-horizontal" onSubmit={this.doSubmit}>
                        <div className="form-group">
                            <label htmlFor="task" className="col-md-2 control-label">Task</label>
                            <div className="col-md-10">
                                <input type="text" 
                                    id="task" 
                                    ref="task" 
                                    className="form-control" 
                                    placeholder="What do you need to do?" 
                                    name="task"
                                    value={this.state.task}
                                    onChange={this.onChange}
                                    />
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-md-10 col-md-offset-2 text-right">
                                <input type="submit" value="Save Item" className="btn btn-primary" />
                            </div>
                        </div>
                    </form>
                </div>
            </div>
        );
    }
}
export default TodoForm
