import { CLASS_NAME } from '../actions/types'
import ClaasJson from '../classroom_data.json'
const initialState = {
    "Maths": 53,
    "Science": 27,
    "English": 62
}


export default function(state = initialState, action) {
    switch(action.type) {
        case CLASS_NAME: 
            console.log(...state)
            return {
                ...state,
                markAverage: action.payload
            }
    }
}
