import React, { Component, Fragment } from 'react'
// import './ProgressBar.css'

class ProgressBar extends Component {

    render() {
        const style = {
            progress: {
                width: this.props.width + '%',
                height: "2px",
                backgroundColor: this.props.color,
                padding:"5px",
                borderRadius:"20px"
            },
            myProgress: {
                width: "90%",
                backgroundColor: "rgb(228, 227, 227)",
                borderRadius: "20px",
                height: "10px",
            }
        }
        return(
            <Fragment>
                <div  className="mt-2 pl-2 pr-2" style={style.myProgress}>
                        <div className="pl-2" style={style.progress}></div>
                    </div>            
                <label>{this.props.width}%</label>

            </Fragment>
        )
    }
}
export default ProgressBar
