import React, { Component } from 'react'

class TodoItem extends Component {
    constructor(props){
        super(props)
        this.state = {
            id: this.props.nodeId
        }
        // this.toggleComplete = this.toggleComplete.bind(this)
        // this.removeNode = this.removeNode.bind(this)
    }
    removeNode(e) {
        e.preventDefault();
        // alert(this.props.nodeId)
        this.props.removeNode(this.state.id);
        // return;
    }
    toggleComplete(e) {
        e.preventDefault();
        this.props.toggleComplete(this.props.nodeId);
        // return;
    }
    updateClass() {

    }
    render(){
        var classes = 'list-group-item clearfix';
        if (this.props.complete === 'true') {
            classes = classes + ' list-group-item-success';
        }
        return (
            <li className={classes}>
                {this.props.task}
                <div className="pull-right" role="group">
                    <button type="button" className="btn btn-xs btn-success img-circle" 
                        onClick={this.toggleComplete}
                    >&#x2713;
                    </button> 
                    <button type="button" className="btn btn-xs btn-danger img-circle" 
                        onClick={this.removeNode}
                    >&#xff38;
                    </button>
                </div>
            </li>
        );
    }
}
export default TodoItem
