import React, { Component, Fragment } from 'react'

import { connect } from 'react-redux'
import PropTypes from 'prop-types'

import ProgressBar from './ProgressBar';
import { getContactus } from '../../actions/classRoomActions'

class Student extends Component {
    constructor(props){
        super(props)
    }
    componentDidMount() {
         this.props.getContactus() 
    }
    render() {
        const { markAverage } = this.props
        console.log(markAverage)
        // Object.keys(markAverage).map((data) => {
        //     markAverage.map((ress) => {
        //         console.log(ress)
        //     })
        // })
        var result = Object.keys(markAverage).map(function(key) {
            return markAverage[key];
          });
        //   console.log(result)
        var newResult = Object.keys(result).map(function(key) {
            return markAverage[key];
        })
        console.log("new", newResult)

        var maths  = 0, 
            englishs = 0,
            science = 0 ,
            student = ''

            newResult.map((res) => {
                if(res !== undefined){
                    console.log(res.students)
                    res.students.map(stud => {
                        student = stud.name
                        console.log("student", stud.name)
                       let dd= Object.keys(stud).map(function(key) {
                            return stud[key];
                          });
                        //   console.log(dd)
                          dd.map((dat) => {
                            //   console.log(dat)
                            if(dat.Maths !== undefined){
                                maths = maths+ dat.Maths
                                englishs = englishs + dat.English
                                science  = science + dat.Science
                            }
                            console.log(dat.Maths, "maths")
                            console.log("Marks", (dat.Maths + dat.English + dat.Science)/3)
                          })
                        //   dd.map(re =>console.log(re.marks))
                        // stud.marks.map(marksData=>{
                        //     console.log('marks', marksData.marks.Maths)
                        // })
                    })
                }

            })
        let totalPercentage =  (maths/3 +  englishs/3 + science/3)/3
        // console.log(maths, maths/3, englishs, englishs/3, science, science/3)

        return(
            <Fragment>
                <div className="card mt-4 pt-4 pl-4 pb-4 pr-4">
                    <div>
                        <span className="">{student}</span>
                        <span className="float-sm-right">{Math.round(totalPercentage)}%</span>
                    </div>
                    <div className="d-flex flex-rows justify-content-betweenw">
                        <span style={{marginInlineEnd: "40px"}}>Math</span>
                        <ProgressBar width={Math.round(maths/3)} color="blue" />
                    </div>
                    <div className="d-flex flex-row justify-content-betweenw">
                        <span style={{marginInlineEnd: "40px"}}>English</span>
                        <ProgressBar width={Math.round(englishs/3)} color="blue" />
                    </div>
                    <div className="d-flex flex-row justify-content-betweenw">
                        <span>Science</span>
                        <ProgressBar width={Math.round(science/3)} color="blue" />
                    </div>
                </div>
            </Fragment>
        )
    }
}
Student.propTypes = {
    getContactus: PropTypes.func.isRequired,
    markAverage: PropTypes.object.isRequired
}
const mapStateToProps = state => ({
    markAverage: state.markAverage
})
export default connect(mapStateToProps, {getContactus})(Student);
