import React, { Component } from 'react'

class TodoItem extends Component {
    constructor(props){
        super(props)
        // this.toggleComplete = this.toggleComplete.bind(this)
        // this.removeNode = this.removeNode.bind(this)
    }
    // removeNode(e) {
