import React, { Component, Fragment } from 'react'
import TextFieldGroup from '../common/TextFieldGroup';


class Todos extends Component {
    constructor(props) {
        super(props);
        this.state = {
            todos:      '',
            todoArray:  []
        }
        this.onChange = this.onChange.bind(this)
    }
    onChange(e) {
        this.setState({[e.target.name]: e.target.value})
    }
    render() {
        return(
            <Fragment>
                <div className="container">
                    <div className="row">
                        <div className="col-md-5 m-auto">
                            <div>
                            <form onSubmit={this.onSubmit}>
                                <span>Task</span>
                                <TextFieldGroup 
                                    placeholder="What do you need to do?"
                                    name="todos"
                                    value={this.state.todos}
                                    onChange={this.onChange}
                                />
                            <input type="submit" value="Save item" />
                            </form>
                            </div>
                            {this.state.todoArray.map((res) => (
                                <ul className="list-group">
                                <li className="list-group-item d-flex  justify-content-between align-items-center mt-2">
                                   <span className="fa fa-user"></span>
                                    <span className="d-flex align-items-start">Cras justo odio</span>
                                    <span className="badge badge-info badge-pill">4</span>
                                </li>

                            </ul>
                            ))}
                        </div>
                    </div>
                </div>
            </Fragment>
        )
    }
}
export default Todos
