import React, { Component } from 'react'
import TodoList from './TodoList'
import TodoForm from './TodoForm'

class TodoBox extends Component {
    constructor(props){
        super(props)
        this.state  = {
            data: [
                {"id":"00001","task":"Wake up","complete":"false"},
                {"id":"00002","task":"Eat breakfast","complete":"false"},
                {"id":"00003","task":"Go to work","complete":"false"}
            ]
        }
        this.handleNodeRemoval = this.handleNodeRemoval.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleToggleComplete = this.handleToggleComplete.bind(this)

    }

    generateId() {
        return Math.floor(Math.random()*90000) + 10000;
    }
    handleNodeRemoval(nodeId) {
        var data = this.state.data;
        data = data.filter(function (el) {
            return el.id !== nodeId;
        });
        this.setState({data});
        return;
    }
    handleSubmit(task) {
        var data = this.state.data;
        var id = this.generateId().toString();
        var complete = 'false';
        data = data.concat([{id, task, complete}]);
        this.setState({data});
    }
    handleToggleComplete(nodeId) {
        var data = this.state.data;
        for (var i in data) {
            if (data[i].id === nodeId) {
                data[i].complete = data[i].complete === 'true' ? 'false' : 'true';
                break;
            }
        }
        this.setState({data});
        return;
    }
    render() {
        return (
            <section class="container vert-offset-top-2">
		  	    <div id="todoBox" class="todoBox col-xs-6 col-xs-offset-3">
                    <div className="well">
                        <h1 className="vert-offset-top-0">To do:</h1>
                        <TodoList   data={this.state.data} 
                                    removeNode={this.handleNodeRemoval} 
                                    toggleComplete={this.handleToggleComplete} 
                        />
                        <TodoForm onTaskSubmit={this.handleSubmit} />
                    </div>
                </div>
		    </section>

        );
    }
}
export default TodoBox
