import React, { Fragment, Component } from 'react'

class ClassAverage extends Component {
    constructor(props){
        super(props)
        this.state={
            hideAverage: "none"
        }
        this.showClassAverage = this.showClassAverage.bind(this)
        this.hideClassAverage = this.hideClassAverage.bind(this)
    }
    showClassAverage(){
        this.setState({
            hideAverage: "block"
        })
    }
    hideClassAverage() {
        this.setState({
            hideAverage: "none"
        })
    }
    render() {
// ============= Css =======================
       const style={
            averageButton : {
                borderRadius: "20px",
                background: "white",
                border: 'none',
                color: "black",
                boxShadow: "0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19)",
                fontSize: "16px",
                marginRight: "40px",
                cursor: "pointer",
                marginTop: "-60px"
            },
            averageDetail:{
                width: "700px",
                height: "65px",
                background: "lightblue",
                // padding: '10px',
                borderRadius: '5px',
                lineHeight: '65px',
                margin: '30px auto 50px'
            }
        }
        return(
            <Fragment>
{/*======================= Class Avarage Component ================================= */}
                <div className="main-data">
                   <div className="title-section">
                        <h2>Class III</h2>
                        <p>Students</p>
                        <div className="btn pull-right card" 
                            style={style.averageButton}
                            onClick={this.showClassAverage}
                        >Show Average</div>
                        <div className="btn pull-right card" 
                            style={style.averageButton}
                            onClick={this.hideClassAverage}
                        >Show Average</div>
                   </div>
                    <div className="text-center" style={{textAlign: "center", display: this.state.hideAverage}}>
                        <div className="text-center" style={style.averageDetail}><p>Class room average performance</p></div>
                    </div>
                    <hr style={{background: "blue", height: "5px", width: "100%"}}/>
                </div>
            </Fragment>
        )
    }
}
export default ClassAverage
