import React, { Component } from 'react'
import TodoItem from './TodoItem';

class TodoList extends Component {
    constructor(props){
        super(props);
        this.removeNode = this.removeNode.bind(this)
        this.toggleComplete = this.toggleComplete.bind(this)
    }
    removeNode(nodeId) {
		this.props.removeNode(nodeId);
		return;
	}
	toggleComplete(nodeId) {
		this.props.toggleComplete(nodeId);
		return;
	}
	render() {
		var listNodes = this.props.data.map(function (listItem) {
                                // <TodoItem key={listItem.id} 
                                //         nodeId={listItem.id} 
                                //         task={listItem.task} 
                                //         complete={listItem.complete} 
                                //         removeNode={this.removeNode} 
                                //         toggleComplete={this.toggleComplete} 
                                // />
                                var classes = 'list-group-item clearfix';
                                    if (listItem.complete === 'true') {
                                        classes = classes + ' list-group-item-success';
                                    }
                                    return (
                                        <li className={classes}>
                                            {listItem.task}
                                            <div className="pull-right" role="group">
                                                <button type="button" className="btn btn-xs btn-success img-circle" 
                                                    onClick={this.toggleComplete.bind(this, this.props.nodeId)}
                                                >&#x2713;
                                                </button> 
                                                <button type="button" className="btn btn-xs btn-danger img-circle" 
                                                    onClick={this.removeNode.bind(this, this.props.nodeId)}
                                                >&#xff38;
                                                </button>
                                            </div>
                                        </li>
        );
                            // );
                        })
		return (
			<ul className="list-group">
				{listNodes}
			</ul>
		);
    }
}
export default TodoList
