import React, { Component, Fragment } from 'react'

import { connect } from 'react-redux'
import PropTypes from 'prop-types'

import ProgressBar from './ProgressBar';
import { getContactus } from '../../actions/classRoomActions'
import ClassData from "../../classroom_data.json";

class Student extends Component {
    constructor(props){
        super(props)
    }
    componentDidMount() {
         this.props.getContactus() 
    }
    render() {
        const { markAverage } = this.props
        console.log(markAverage)
        var result = Object.keys(markAverage).map(function(key) {
            return markAverage[key];
          });
          var total  = 0, totalSubject = 0

        let a = result.map((res, i) => {
            totalSubject = 0
                if(res.Maths !== undefined){
                    total = total+res.Maths + res.English + res.Science
                    totalSubject = total
                    total = 0                  
                }            
        })
       console.log(totalSubject)
        return(
            <Fragment>
                <div className="card mt-4 pt-4 pl-4 pb-4 pr-4">
                    <div>
                        <span className="">Student1</span>
                        <span className="float-sm-right">{this.props.totalPercentage}</span>
                    </div>
                    <div className="d-flex flex-row justify-content-betweenw">
                        <span>Math{totalSubject}</span>
                        <span className="ml-auto">
                            <ProgressBar width={23} color="blue" />
                        </span>
                        {/* <span className="content-justify">{this.props.totalPercentage}</span> */}
                    </div>
                </div>
            </Fragment>
        )
    }
}
Student.propTypes = {
    getContactus: PropTypes.func.isRequired,
    markAverage: PropTypes.object.isRequired
}
const mapStateToProps = state => ({
    markAverage: state.markAverage
})
export default connect(mapStateToProps, {getContactus})(Student);
