import React, { Component } from 'react'
import TodoItem from './TodoItem';

class TodoList extends Component {
    constructor(props){
        super(props);
        // this.removeNode = this.removeNode.bind(this)
        // this.toggleComplete = this.toggleComplete.bind(this)
        this.helloClick = this.helloClick.bind(this)
    }
    helloClick(){
        alert("ewdew")
    }
    removeNodes(nodeId) {
		this.props.removeNoded(nodeId);
		return;
	}
	toggleCompletes(nodeId) {
		this.props.toggleCompleted(nodeId);
		return;
	}
	render() {
		var listNodes = this.props.data.map((listItem) => (
                                // <TodoItem key={listItem.id} 
                                //         nodeId={listItem.id} 
                                //         task={listItem.task} 
                                //         complete={listItem.complete} 
                                //         removeNode={this.removeNode} 
                                //         toggleComplete={this.toggleComplete} 
                                // />
                                // alert(JSON.stringify(listItem))
                                // var classes = 'list-group-item clearfix';
                                //     if (listItem.complete === 'true') {
                                //         classes = classes + ' list-group-item-success';
                                //     }

                                <li className={`classes`}>
                                    {listItem.task}
                                    <div className="pull-right" role="group">
                                        <button type="button" className="btn btn-xs btn-success img-circle" 
                                           onClick={this.toggleCompletes.bind(this, listItem.id)}
                                        // onChange={this.toggleCompleted}
                                        >&#x2713;
                                        </button> 
                                        <button type="button" className="btn btn-xs btn-danger img-circle" 
                                            onClick={this.removeNodes.bind(this, listItem.id)}
                                        >&#xff38;
                                        </button>
                                        {/* <span onClick={this.helloClick}>Hello</span> */}
                                    </div>
                                </li>
                            // );
                        ))
		return (
			<ul className="list-group">
				{listNodes}
			</ul>
		);
    }
}
export default TodoList
