import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { registerUser } from '../../actions/authAction';  
import { withRouter } from 'react-router-dom';
import TextFieldGroup from '../common/TextFieldGroup';
import Helmet from 'react-helmet';

class Register extends Component {
  constructor() {  
    super();
    this.state= {
      name: '',
      email: '',
      password: '',
      password2: '',  
      errors: {}
    };
    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }
  componentWillReceiveProps(nextProps){
    if(nextProps.errors){
      this.setState({errors: nextProps.errors})
    }
  }
  onSubmit(e){
    e.preventDefault();
    const newUser = {
      name: this.state.name,
      email: this.state.email,
      password: this.state.password,
      password2: this.state.password2
    }; 
    // console.log(newUser); 
    this.props.registerUser(newUser, this.props.history);
   console.log(this.props.history)
    }
  onChange(e){
    this.setState({ [e.target.name]: e.target.value});
  }
  render(){
    const { errors } = this.state;

    return (
      <div>
        <Helmet><title>User Register</title></Helmet> 
        <div className="container-fluid">
          <div className="banner-section">
              <div className="banner-title">
                  <h1>Login/Register</h1>
              </div>
          </div>
        </div>
        <div className="register">
          <div className="container">
            <div className="row">         
              <div className="col-md-3"></div>
                <div className="col-md-6 m-auto">
                  <div className="form-layout form-block">
                    <h1 className="text-center heading-blk">Sign Up</h1>
                    <p className="lead text-center">
                      Create an Account for New User
                    </p>
                    <form onSubmit={this.onSubmit}>
                        <div className="input-group" style={{marginBottom: "15px"}}>
                          <span className="input-group-addon" id="basic-addon1"> 
                            <i className="glyphicon glyphicon-user"></i>
                          </span> 
                          <TextFieldGroup
                            placeholder="Name"
                            name="name"
                            type="name"
                            value={this.state.name}
                            onChange={this.onChange}
                            required
                          />
                        </div>
                        <h5>{errors.name}</h5>
                        <div className="input-group" style={{marginBottom: "15px"}}>
                        <span className="input-group-addon" id="basic-addon1">@</span>
                          <TextFieldGroup
                            placeholder="Email Address"
                            name="email"
                            type="email"
                            value={this.state.email}
                            onChange={this.onChange}
                            required
                          />                           
                        </div> 
                        <h5>{errors.email}</h5> 
                        <div className="input-group" style={{marginBottom: "15px"}}>
                          <span className="input-group-addon" id="basic-addon1"> 
                            <i className="glyphicon glyphicon-lock"></i>
                          </span>              
                          <TextFieldGroup
                            placeholder="Password"
                            name="password"
                            type="password"
                            value={this.state.password}
                            onChange={this.onChange}
                            required
                          />
                        </div>
                        <h5>{errors.password}</h5>
                        <div className="input-group" style={{marginBottom: "15px"}}>
                          <span className="input-group-addon" id="basic-addon1"> 
                            <i className="glyphicon glyphicon-lock"></i>
                          </span> 
                          <TextFieldGroup
                            placeholder="confirm Password"
                            name="password2"
                            type="password"
                            value={this.state.password2}
                            onChange={this.onChange}
                            required
                          />
                        </div>
                        <h5>{errors.password2}</h5>
                      <input type="submit" value="Signup" className="btn btn-info btn-block mt-4" />
                    </form></div>
                  </div>           
                <div className="col-md-3">
                </div>  
              </div>         
          </div>
        </div>
      </div>
    );
  }
} 
Register.propTypes = {
  registerUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth,
  errors: state.errors
})

export default connect(mapStateToProps, {registerUser})(withRouter(Register));
