import './ProgressBar.css'

class ProgressBar extends Component {

    render() {
        const style = {
            progress: {
                width: this.props.width + '%',
                height: "12px",
                backgroundColor: this.props.color,
                padding:"5px",
                borderRadius:"20px"
            },
            myProgress: {
                width: "100%",
                backgroundColor: "rgb(228, 227, 227)",
                borderRadius: "20px"
            }
        }
        return(
            <Fragment>
                {/* <div className="d-flex">
                    <div className="myProgress">
                        <div style={style.progress} className="mt-4"></div>
                    </div>                  
                    <div>{this.props.width}%</div>
                </div> */}
                <span><div className="myProgress">
                        <div style={style.progress} className="mt-4"></div>
                    </div>
                </span>
                <label><div>{this.props.width}%</div></label>

            </Fragment>
        )
    }
}
export default ProgressBar
