import React, { Fragment, Component } from 'react'

class ClassAverage extends Component {

    render() {
// ============= Css =======================
       const style={
            averageButton : {
                borderRadius: "20px",
                background: "white",
                border: 'none',
                color: "black",
                boxShadow: "0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19)",
                fontSize: "16px",
                marginRight: "40px"
            },
            averageDetail:{
                width: "500px",
                height: "50px",
                background: "lightblue",
                padding: '10px',
                borderRadius: '20px'
            }
        }
        return(
            <Fragment>
{/*======================= Class Avarage Component ================================= */}
                <h2>Class III</h2>
                <p>Students</p>
                <div className="btn pull-right card" style={style.averageButton}>Show Average</div>
                <div className="text-center" style={style.averageDetail}><p>Class room average performance</p></div>
                <br/><br/>
                <hr style={{background: "blue", height: "5px", width: "100%"}}/>
            </Fragment>
        )
    }
}
export default ClassAverage
