import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { loginUser } from '../../actions/authAction';
import TextFieldGroup from '../common/TextFieldGroup';
import Helmet from 'react-helmet';

import { Link } from "react-router-dom"

class Login extends Component {
  constructor() {
    super();
    this.state = {
      email: '',
      password: '',
      errors: {}
    };

    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

  componentDidMount() {
    if (this.props.auth.isAuthenticated) {
      this.props.history.push('/dashboard');
    }
  }

  // componentWillReceiveProps(nextProps) {
  //   if (nextProps.auth.isAuthenticated) {
  //     this.props.history.push('/dashboard');
  //   }

  //   if (nextProps.errors) {
  //     this.setState({ errors: nextProps.errors });
  //   }
  // }

  onSubmit(e) {
    e.preventDefault();
    const userData = {
      email: this.state.email,
      password: this.state.password
    };
    this.props.loginUser(userData);
  }

  onChange(e) {
    this.setState({ [e.target.name]: e.target.value });
  }

  render() {
    const { errors } = this.state;

    return (
      <div>
        <Helmet><title>User Login</title></Helmet> 
        <div className="container-fluid">
            <div className="banner-section">
                <div className="banner-title">
                    <h1>Login/Register</h1>
                </div>
            </div>
        </div>
        <div className="login">
          <div className="container">
            <div className="row">
              <div className="col-md-6 m-auto">
              <div className="form-layout form-block">
                <h1 className="display-4 heading-blk">Log In</h1>
                <p className="lead text-center">
                  Sign in with existing user
                </p>
                <form onSubmit={this.onSubmit}>
                <div className="input-group" style={{marginBottom: "15px"}}>
                    <span className="input-group-addon" id="basic-addon1">@</span>
                    <TextFieldGroup
                    style={{color: "red !important", textAlign: "left", paddingTop: "2px",}}
                      placeholder="Email Address"
                      name="email"
                      type="email"
                      value={this.state.email}
                      onChange={this.onChange}
                      required
                    />
                  </div>
                  <h5>{errors.email}</h5>
                  <div className="input-group" style={{marginBottom: "15px"}}>
                    <span className="input-group-addon" id="basic-addon1"> 
                      <i className="glyphicon glyphicon-lock"></i>
                    </span>
                    <TextFieldGroup
                      placeholder="Password"
                      name="password"
                      type="password"
                      value={this.state.password}
                      onChange={this.onChange}
                      required
                    />
                  </div>
                  <h5>{errors.password}</h5>
                  <div className="col-sm-6">
                    <input type="submit" className="btn btn-info btn-block btn-sm" value="Log In" />
                  </div>
                  <div className="col-sm-6">
                  <br/>
                  <Link to='/forget-password' className="mt-4">
                    <i className="glyphicon glyphicon-lock"></i>
                    Forgot password
                  </Link><br/>
                  </div>
                </form><br/>               
                </div>
              </div>
              <div className="col-md-6">
              <div className="form-block">
              <br/><br/>
              <h1 className="text-center heading-blk">Sign Up</h1>
                <p className="lead text-center">
                  Create an account for new User
                </p>
                <center>
              <Link className="btn btn-info btn-login" to="/register">Register</Link>
              </center>
              </div>
            </div>
            </div>          
          </div>
        </div>
      </div>
    );
  }
}

Login.propTypes = {
  loginUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth,
  errors: state.errors
});

export default connect(mapStateToProps, { loginUser })(Login);
