import { GET_ERRORS } from './types'
import axios from 'axios'

export const loginUser = () => dispatch => {
    axios
        .post('/', )
        .then()
        .catch(err => 
            dispatch({
                type: GET_ERRORS,
                payload: err.response.data
            })
        )
}
