import React, { Component, Fragment } from 'react'
import TextFieldGroup from '../common/TextFieldGroup';

class Todos extends Component {
    constructor(props) {
        super(props);
        this.state = {
            todos:      '',
            todoArray:  ["vivek", "ededwe","dwe"],
            editTodos: false
        }
        this.onChange = this.onChange.bind(this)
        this.onSubmit = this.onSubmit.bind(this)
    }
    onChange(e) {
        this.setState({[e.target.name]: e.target.value})
    }
    onSubmit(e) {
        e.preventDefault()
        this.setState({editTodos: false})
        if(this.state.todos === ''){
            alert("Please enter value")
        } else {
            this.state.todoArray.push(this.state.todos)
        this.setState({ todos: '' })
        }        
    }
    onClickDelete(index) {
       this.state.todoArray.splice(index, 1)
       this.setState({todos: ''})
    }
    onClickEdit(index, res) {
        alert(index, itemValue)
        this.setState({editTodos: true, todos: res})
        this.state.todoArray.splice(index, this.state.todos)
    }

    render() {
        console.log(this.state.todoArray)
        if(this.state.editTodos === true){
            return <form onSubmit={this.onSubmit}>                                
                        <TextFieldGroup 
                            placeholder="What do you need to do?"
                            name="todos"
                            value={this.state.todos}
                            onChange={this.onChange}
                        />
                        <input type="submit" className="btn btn-primary" value="Save item" />
                    </form>
        }
        return(
            <Fragment>
                <div className="container">
                    <div className="row">
                        <div className="col-md-6 m-auto">
                            <div style={{background: "#cccc"}}>
                                <h4>Task</h4>
                                {this.state.todoArray.map((res, index) => (
                                    <ul className="list-group" key={index}>
                                        <li className="list-group-item d-flex  justify-content-between align-items-center">
                                            <span className="d-flex align-items-start">{res}</span>
                                            <span title="Edit Item" className="fa fa-edit" onClick={this.onClickEdit.bind(this, res, index)}></span>
                                            <span title="Revmove Item" className="fa fa-close" onClick={this.onClickDelete.bind(this, index)}></span>
                                        </li>                                
                                    </ul>
                                ))}
                                <div>
                                    <form onSubmit={this.onSubmit}>                                
                                        <TextFieldGroup 
                                            placeholder="What do you need to do?"
                                            name="todos"
                                            value={this.state.todos}
                                            onChange={this.onChange}
                                        />
                                        <input type="submit" className="btn btn-primary" value="Save item" />
                                    </form>
                                </div>       
                            </div>                         
                        </div>
                    </div>
                </div>
            </Fragment>
        )
    }
}
export default Todos
